site_customization.stylesheet:
.topic-list.categories th, .topic-list.categories td {
    padding: 0 15px 0 15px;
}
.topic-list.categories .has-description td.category {
    padding-top: 20px;
}
//add some padding to posts so that avatars are not on the side
.topic-avatar
{
    padding-right: 10px;
}
.topic-body{
  margin-top: 10px;
  padding-left: 20px;
  padding-right: 20px;
}
.topic-list.categories > tbody > tr > td > div > .clearfix {
    height: 0px;
}
#list-area {
    margin-bottom: 0px;
}
.small-action {
    padding-left: 56px;
}

site_customization.mobile_stylesheet:
.category-list-item.category {
    margin-bottom: 8px;
}
#list-area {
    margin-bottom: 0px;
}
.topic-avatar
{
    padding-left: 10px;
}
.topic-body{
    margin-top: -6px;
  padding-left: 20px;
  padding-right: 20px;
}
